f = 5; ##Hzamp = 19; ##voltsTs = 0.0001; ##Sampling Timet = 0:Ts:3-Ts;#Generating a square wave..signal = amp * sign(sin(2*pi*f*t));#Plotting the cosine wave.subplot(3,1,1);plot(t, signal);xlabel('Time')ylabel('Amplitude')#Performing Fourier Transform using the FFT algorithm.y = fft(signal);Fs = 1/Ts;f = (0:length(y)-1)*Fs/length(y);#Plotting the frequency v/s magnitude graph.subplot(3,1,2);plot(f, abs(y));xlabel('Frequency (Hz)')ylabel('Magnitude')title('freq v/s magnitude')#Performing Inverse Fourier Transformz = ifft(y);subplot(3,1,3);plot(t, z);xlabel('Time(t)')ylabel('Original signal from IFFT');